SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for flyl_article
-- ----------------------------
DROP TABLE IF EXISTS `flyl_article`;
CREATE TABLE `flyl_article`  (
  `id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章ID',
  `cate_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分类ID',
  `cate_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分类名称',
  `sort_id` varchar(32) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '所属栏目ID',
  `title` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章标题',
  `title_colour` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题颜色',
  `titlefont_size` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题大小',
  `titlefont_type` varchar(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题字型',
  `full_title` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '完整标题',
  `subhead` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '副表题',
  `intro` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '简介',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '文章内容',
  `author` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '作者',
  `copy_from` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '来源',
  `inputer` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '录入者',
  `http_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '转向链接',
  `keyword` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关键字',
  `hit_number` int(11) NULL DEFAULT NULL COMMENT '点击数',
  `post_number` int(11) NULL DEFAULT NULL COMMENT '评论数',
  `ontop` int(11) NULL DEFAULT NULL COMMENT '是否置顶',
  `iselite` int(11) NULL DEFAULT NULL COMMENT '是否推荐',
  `status` int(1) NULL DEFAULT NULL COMMENT '状态：0，正常，2删除',
  `add_time` datetime(0) NULL DEFAULT NULL COMMENT '添加时间',
  `update_time` datetime(0) NULL DEFAULT NULL COMMENT '更新时间',
  `create_time` datetime(0) NULL DEFAULT NULL COMMENT '生成时间',
  `last_post_time` datetime(0) NULL DEFAULT NULL COMMENT '最后评论时间',
  `owner_tag` int(10) NULL DEFAULT NULL COMMENT '自定义属性',
  `html_path` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'html地址',
  `files_path` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '上传文件地址',
  `temp_path` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '模板地址',
  `thumb` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '缩略图地址',
  `html_status` int(2) NULL DEFAULT NULL COMMENT '生成状态',
  `article_status` int(2) NULL DEFAULT NULL COMMENT '文章状态',
  `media_type` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1' COMMENT '媒介类型：1，图文，2，视频',
  `tag` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文章标签',
  `relay_number` int(10) NULL DEFAULT 0 COMMENT '转发数',
  `fav_number` int(10) NULL DEFAULT 0 COMMENT '收藏数',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '文章' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of flyl_article
-- ----------------------------
INSERT INTO `flyl_article` VALUES ('1527078344354213888', '1526903736317730816', '医院', '', '医院成功范例', '1', '', '宋体', '', '', '', '<!--HTML--><p>医院是指按照法律法规和行业规范，为病员开展必要的医学检查、治疗措施、护理技术、接诊服务、康复设备、救治运输等服务，以救死扶伤为主要目的的医疗机构，其服务对象不仅包括有症状的病员和伤员，也包括不能自理或活动受限有医疗护理依赖的老年人，法医评定有医疗护理依赖或病情不稳定需要长期康复经常观察检查的重度病伤员，或有其他特定的情况和人群，如健康人（如孕妇、产妇、新生儿）以及完全健康的人（如来医院进行体格检查或口腔清洁的人），最初设立时，是供人避难，还备有娱乐节目，使来者舒适，有招待意图，后来，才逐渐成为收容和治疗病人的专门机构。</p><p><span class=\"ref\" data-ctrid=\"sZ9H47FDRMLT\">《中华人民共和国2021年国民经济和社会发展统计公报》显示：2021年，</span>年末全国共有医院3.7万个<span class=\"ref\" data-ctrid=\"sZ9H47FDRMLT\">。</span></p><p><img src=\"/js/userfiles/system/images/20220530/蝴蝶_$1653906225594071372.jpg\" title=\"蝴蝶_$1653906225594071372.jpg\" alt=\"蝴蝶.jpg\"/><img src=\"/js/userfiles/system/images/20220530/生活中的旋转_$1653906407144003623.jpg\" title=\"生活中的旋转_$1653906407144003623.jpg\" alt=\"生活中的旋转.jpg\"/><img src=\"/js/userfiles/system/images/20220531/蝴蝶_$1653927326615098684.jpg\" title=\"蝴蝶_$1653927326615098684.jpg\" alt=\"蝴蝶.jpg\"/><video class=\"edui-upload-video  vjs-default-skin video-js\" controls=\"\" preload=\"none\" width=\"420\" height=\"280\" src=\"/js/userfiles/system/videos/20220531/轻映录屏 2022-05-14 11-12-26_$1653927348101060869.mp4\" data-setup=\"{}\"><source src=\"/js/userfiles/system/videos/20220531/轻映录屏 2022-05-14 11-12-26_$1653927348101060869.mp4\" type=\"video/mp4\"/></video></p>', '测试', '', '', '', '医院', 1067, NULL, 0, 1, 0, NULL, '2022-05-31 00:15:54', '2022-05-31 00:15:54', NULL, NULL, NULL, '', '', '/js/userfiles/fileupload/202205/1527144862849884160.jpg', NULL, NULL, '1', '最新', 6, 124);
INSERT INTO `flyl_article` VALUES ('1527145539923795968', '1526903823005605888', '建筑', '', '中国经济无惧风浪激流', '2', '12', '仿宋', '中国经济无惧风浪激流', '匠心工程', '中国经济无惧风浪激流', '<!--HTML--><p>今年3月份以来,受国际地缘政治复杂演变、<em style=\"font-style: normal; color: rgb(51, 51, 51);\">国内</em>疫情起伏反复等超预期变化影响,我国<em style=\"font-style: normal; color: rgb(51, 51, 51);\">经济</em>发展环境的复杂性、严峻性、不确定性上升,稳增长、稳就业、稳物价面临新挑战。从最新发布...</p><p><a class=\"group-source-wrapper_XvbsB\" href=\"http://www.baidu.com/link?url=M0vNtjhg2DxbKtpGbVKkb-ywjwo1DBsV8gGsJwgHIyZWhQIx5glqpQYWTaFbkoM-ZH-iqt9ZTrY6xCd18dPunCw7nL536lmVzlz6lg0PuPy\" target=\"_blank\" style=\"color: rgb(36, 64, 179); text-decoration: none; cursor: auto; font-family: Arial, sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\"></a></p><p><a class=\"group-source-wrapper_XvbsB\" href=\"http://www.baidu.com/link?url=M0vNtjhg2DxbKtpGbVKkb-ywjwo1DBsV8gGsJwgHIyZWhQIx5glqpQYWTaFbkoM-ZH-iqt9ZTrY6xCd18dPunCw7nL536lmVzlz6lg0PuPy\" target=\"_blank\" style=\"color: rgb(36, 64, 179); text-decoration: none; cursor: auto; font-family: Arial, sans-serif; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255);\"><span class=\"c-color-gray group-source-site_2blPt\" style=\"color: rgb(98, 102, 117); cursor: pointer;\"><img class=\"group-source-icon_3iDHz\" src=\"/js/userfiles/system/images/20220519/1652934813464090483.jpg\" alt=\"\" aria-hidden=\"true\" style=\"border: 0px; vertical-align: middle; width: 16px; height: 16px; bor