INSERT INTO `flyl_article` VALUES ('1527146588206194688', '1526903477030051840', '行业知识', '', '国内新冠肺炎疫情呈多', '5', '国内', '强化', '国内新冠肺炎疫情呈多点散发态势，防控形势依旧严峻', '国内新冠肺炎疫情呈多点散发态势，防控形势依旧严峻', '国内新冠肺炎疫情呈多点散发态势，防控形势依旧严峻', '<!--HTML--><p><span style=\"color: rgb(51, 51, 51); font-family: arial; font-size: 16px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">国内新冠肺炎疫情呈多点散发态势，防控形势依旧严峻</span></p>', '国内新冠肺炎疫情呈多点散发态势，防控形势', '国内新冠肺炎疫情呈多点散发态势，防控形势', '', '', '国内新冠肺炎疫情呈多点散发态势，防控形势依旧严峻', 8830, NULL, NULL, 0, 0, NULL, NULL, '2022-05-18 14:07:01', NULL, NULL, NULL, '', '', '/js/userfiles/fileupload/202205/1527146556337872896.jpeg', NULL, NULL, '1', '最新', 13, 55);
